}

return(
<Fragment>
<h1 className="text-center mt-5">Todo List</h1>
<form className="mt-5 d-flex">
    <input type="text" className="form-control mx-1" onChange={getChanges}/>
    <button className="btn btn-success"> Add</button>
</form>
</Fragment>
)
}


